import path from 'path';
import { fileURLToPath } from 'url';
import TemplateHelperPlugin from '@magnolia/cli-template-helper';
import { createRequire } from 'module';
import { Option } from 'commander';
import { initI18n } from '@magnolia/cli-helper/i18n';
const __dirname = path.dirname(fileURLToPath(import.meta.url));
const requireFn = createRequire(import.meta.url);
const pkg = requireFn('./package.json');
export default class CreateComponentPlugin extends TemplateHelperPlugin {
    constructor(args) {
        super(args, 'components');
        this.name = 'create-component';
        this.usage = '[name] [options]' + '\nParameter: [name] The name of the component.';
        this.version = pkg.version;
        const i18nInstance = initI18n(this.name, 'translation', path.join(__dirname, 'lib/locales'));
        this.description = i18nInstance.t('description');
        this.options = [
            new Option('-a, --available [[lm-name:]page-template[@area]]', i18nInstance.t('option-available')),
            new Option('-lm, --light-module [name]', i18nInstance.t('option-lightModule-description')),
            new Option('-p, --prototype [name]', i18nInstance.t('option-prototype-description')),
        ];
    }
}
